package billboards.viewer;

import billboards.controlPanel.ServerCommunication;
import billboards.shared.BillboardDrawer;
import billboards.shared.TCPMessage;
import billboards.shared.XmlInterpreter;
import org.xml.sax.SAXException;

import javax.swing.*;
import javax.xml.parsers.ParserConfigurationException;
import java.awt.event.ActionEvent;
import java.awt.event.ActionListener;
import java.io.IOException;
import java.util.Arrays;

public class BillboardViewer{

    /**
     * Set the default billboard values. These are the values of the billboard that will appear when there is an error, connection to the server is lost or there is no billboard scheduled
     */
    private static String DefaultBackgroundColour = "GRAY";
    private static String[] DefaultElement1 = {"Image", "DATA", "",
            "30", "45", "50", "60", null};
    private static String[] DefaultElement2 = {"Text", "Connection to server lost.", "50", "20", "25", "32", "BLUE", "100"};
    private static String[] DefaultElement3 = {"Text", "System Will continue to attempt connection. Please Stand By.", "50", "40", "85", "34", "BLACK", "40"};

    private static String CurrentBillboardID;
    private static String CurrentBillboardBackgroundColour;
    private static String[] CurrentBillboardElement1 = new String[8];
    private static String[] CurrentBillboardElement2 = new String[8];
    private static String[] CurrentBillboardElement3 = new String[8];


    public static void BillboardViewer(){
        //Need to start it once or else the elements do not appear
        String[] StartElement1 = {"None", null, null, null, null, null, null, null}; String[] StartElement2 = {"None", null, null, null, null, null, null, null}; String[] StartElement3 = {"None", null, null, null, null, null, null, null};

        //Initialise the server connection and the billboard drawer
        RequestServer("");
        ServerConnection();

        ActionListener TimerListener = new ActionListener() {
            @Override
            public void actionPerformed(ActionEvent e) {
                ServerConnection();
            }
        };
        Timer timer = new Timer(15000, TimerListener);
        timer.setInitialDelay(15000);
        timer.start();
    }

    private static void RequestServer(String testString) {
        System.out.println("Requesting from server");
        // Added 22/06/2020 By Jack Benson
        boolean testing = !testString.equals("");
        String message = TCPMessage.build(TCPMessage.type.REQUEST_VIEWER_REQUEST);
        String response = ServerCommunication.getResponse(message);
        String[] tempFields = TCPMessage.receive(response);
        String[] fields = new String[2];

        if(testing) {
            fields[1] = testString;
            fields[0] = "" + TCPMessage.type.RESPONSE_SUCCESS;
        } else {
            fields = tempFields;
        }

        if(!fields[0].equals("Error communicating with server")) {
            if (Integer.parseInt(fields[0]) == TCPMessage.type.RESPONSE_SUCCESS) {
                try {
                    XmlInterpreter interpreter = new XmlInterpreter(fields[1], false);
                    String[][] result = interpreter.xmlToStringMatrix();
                    CurrentBillboardBackgroundColour = DefaultBackgroundColour;
                    String[][] tempElement = new String[3][8];

                    for (String[] res : result) {
                        if (res[0].equals("information")) {
                            tempElement[0][0] = "Text";
                            tempElement[0][1] = res[1];

                            tempElement[0][2] = "50";
                            tempElement[0][3] = "40";
                            tempElement[0][4] = "85";
                            tempElement[0][5] = "34";

                            tempElement[0][6] = "BLACK";

                            tempElement[0][7] = "40";

                        } else if (res[0].equals("message")) {
                            tempElement[1][0] = "Text";
                            tempElement[1][1] = res[1];

                            tempElement[1][2] = "50";
                            tempElement[1][3] = "20";
                            tempElement[1][4] = "25";
                            tempElement[1][5] = "32";

                            tempElement[1][6] = "BLACK";

                            tempElement[1][7] = "100";
                        } else if (res[0].equals("picture")) {
                            tempElement[2][0] = "Image";
                            tempElement[2][1] = res[2].toUpperCase();
                            tempElement[2][2] = res[3];

                            tempElement[2][3] = "30";
                            tempElement[2][4] = "45";
                            tempElement[2][5] = "50";
                            tempElement[2][6] = "60";

                            tempElement[2][7] = null;
                        }
                    }

                    if(tempElement[0][0] != null) CurrentBillboardElement1 = tempElement[0];
                    else CurrentBillboardElement1 = new String[]{"Text", " ", "50", "20", "25", "32", "BLUE", "100"};
                    if(tempElement[1][0] != null) CurrentBillboardElement2 = tempElement[1];
                    else CurrentBillboardElement2 = new String[]{"Text", " ", "50", "20", "25", "32", "BLUE", "100"};
                    if(tempElement[2][0] != null) CurrentBillboardElement3 = tempElement[2];
                    else CurrentBillboardElement3 = new String[]{"Text", " ", "50", "20", "25", "32", "BLUE", "100"};


                    BillboardDrawer.BillboardDrawerMain(CurrentBillboardBackgroundColour, CurrentBillboardElement1, CurrentBillboardElement2, CurrentBillboardElement3);
                } catch (ParserConfigurationException | IOException | SAXException | java.lang.NumberFormatException e) {
                    e.printStackTrace();
                    BillboardDrawer.BillboardDrawerMain(DefaultBackgroundColour, DefaultElement1, DefaultElement2, DefaultElement3);
                }
            }
            else {
                BillboardDrawer.BillboardDrawerMain(DefaultBackgroundColour, DefaultElement1, DefaultElement2, DefaultElement3);
            }
        }
        else {
            BillboardDrawer.BillboardDrawerMain(DefaultBackgroundColour, DefaultElement1, DefaultElement2, DefaultElement3);
        }
    }

    private static void ServerConnection(){
        BillboardDrawer.CloseWindow();
        RequestServer("");
    }
}
